import {
  takeEvery,
  put
} from 'redux-saga/effects'

import {
  GET_INIT_LIST
} from './actionTypes'

import {
  getInitDataAction
} from './createActions'

import axios from 'axios'


function* getInitListData() {
  try {
    const res = yield axios.get('http://localhost:3333/student')
    const action = getInitDataAction(res.data)
    yield put(action)
  } catch (error) {
    console.log('error: ', error)
  }
}

function* mySaga() {
  yield takeEvery(GET_INIT_LIST, getInitListData);
}

export default mySaga;
